import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { FormControl, InputLabel, Select, MenuItem } from "@material-ui/core";
import { useSelector, useDispatch } from "react-redux";
import {
  setEspecialidad,
  getListaEspecialidad,
} from "actions/EspecialidadActions";
import { useSnackbar } from "notistack";
import { url_servidor } from "Utils/constants";

export default function ConfiguracionHorarioForm() {
  const classes = useStyles();
  const dispatch = useDispatch();
  const { enqueueSnackbar } = useSnackbar();
  const [intervalo, setIntervalo] = useState("");
  const [cdConfiguracion, setCdConfiguracion] = useState(0);
  const [horaDesde, setHoraDesde] = useState();
  const [horaHasta, setHoraHasta] = useState();
  const especialidad = useSelector((state) => state.horario.horario);

  const guardarHorario = () => {
    if (intervalo !== "") {
      fetch(url_servidor + "especialidad", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          cd_configuracion: cdConfiguracion,
          nombre: nombreEspecialidad,
        }),
      }).then(function (response) {
        if (response.status === 200) {
          enqueueSnackbar("Se guardÃ³ la Especialidad", {
            variant: "success",
          });
          dispatch(getListaEspecialidad());
        } else {
          enqueueSnackbar("Error al guardar la Especialidad", {
            variant: "error",
          });
        }
      });
    } else {
      enqueueSnackbar("No puede dejar el intervalo en blanco", {
        variant: "warning",
      });
    }
  };

  useEffect(() => {
    setNombreEspecialidad(especialidad.nombre);
    setCdEspecialidad(especialidad.cd_especialidad);
  }, [especialidad]);

  return (
    <div>
      <Grid container>
        <Grid item lg={10} xs={8} md={8} sm={8} className={classes.headerForm}>
          Editar Horario
        </Grid>
        <Grid
          item
          lg={2}
          xs={4}
          md={4}
          sm={4}
          className={classes.buttonForm}
        ></Grid>
      </Grid>
      <Grid container className={classes.gridForm}>
        <Grid item xs={12} md={12} sm={12} lg={12}>
          <TextField
            variant="outlined"
            margin="normal"
            label={"Especialidad"}
            fullWidth
            value={nombreEspecialidad}
            onChange={(event) => setNombreEspecialidad(event.target.value)}
          />
        </Grid>
        <Grid item xs={12} md={12} sm={12} lg={12}>
          <Grid container>
            <Grid item xs={12} sm={6} md={6} lg={6}>
              <FormControl
                variant="outlined"
                fullWidth
                className={classes.formControl}
              >
                <InputLabel id="demo-simple-select-outlined-label">
                  Obra Social
                </InputLabel>
                <Select
                  labelId="demo-simple-select-outlined-label"
                  id="demo-simple-select-outlined"
                  label="InstituciÃ³n"
                  fullWidth
                  value={obraSocial}
                  onChange={(event) => setObraSocial(event.target.value)}
                >
                  {listaObrasSociales &&
                    listaObrasSociales.map((obraSocial) => {
                      return (
                        <MenuItem value={obraSocial.cd_os}>
                          {obraSocial.nombre}
                        </MenuItem>
                      );
                    })}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={6} md={6} lg={6}>
              <FormControl
                variant="outlined"
                fullWidth
                className={classes.formControl}
              >
                <InputLabel id="demo-simple-select-outlined-label">
                  Obra Social
                </InputLabel>
                <Select
                  labelId="demo-simple-select-outlined-label"
                  id="demo-simple-select-outlined"
                  label="InstituciÃ³n"
                  fullWidth
                  value={obraSocial}
                  onChange={(event) => setObraSocial(event.target.value)}
                >
                  {listaObrasSociales &&
                    listaObrasSociales.map((obraSocial) => {
                      return (
                        <MenuItem value={obraSocial.cd_os}>
                          {obraSocial.nombre}
                        </MenuItem>
                      );
                    })}
                </Select>
              </FormControl>
            </Grid>
          </Grid>
        </Grid>
        <Grid item>
          <Button
            variant="contained"
            color="primary"
            onClick={guardarEspecialidad}
          >
            Guardar
          </Button>
        </Grid>
      </Grid>
    </div>
  );
}
const useStyles = makeStyles((theme) => ({
  headerForm: {
    textAlign: "center",
    paddingTop: 20,
  },
  buttonForm: {
    paddingTop: 10,
  },
  gridForm: {
    padding: 10,
  },
}));
