import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import { FormControl, InputLabel, Select, MenuItem } from "@material-ui/core";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { useSelector, useDispatch } from "react-redux";
import { setPlan, getListaPlanes } from "actions/PlanActions";

import { useSnackbar } from "notistack";
import { url_servidor } from "Utils/constants";
import { setTipoServicio } from "actions/TipoServicioActions";
import { setCostoServicio } from "actions/CostoServicioActions";

export default function CostoServicioForm() {
  const classes = useStyles();
  const dispatch = useDispatch();
  const { enqueueSnackbar } = useSnackbar();
  const [costo, SetCosto] = useState(0);
  const [cdPlan, setCdPlan] = useState(0);
  const [cdTipoServicio, setCdTipoServicio] = useState(0);
  const [cdServicio, setCdServicio] = useState(0);
  const costoServicio = useSelector(
    (state) => state.costoServicio.costoServicio
  );
  const listaServicios = useSelector((state) => state.servicio.listaServicios);
  const listaPlanes = useSelector((state) => state.plan.listaPlanes);
  const listaTipoServicio = useSelector(
    (state) => state.tipoServicio.listaTipoServicios
  );

  const guardarPlan = () => {
    if (nombrePlan !== "" && obraSocial !== 0) {
      fetch(url_servidor + "costoServicio", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          cd_plan: cdPlan,
          nombre: nombrePlan,
          obraSocial: { cd_os: obraSocial, nombre: "" },
        }),
      }).then(function (response) {
        if (response.status === 200) {
          enqueueSnackbar("Se guard贸 el Plan", {
            variant: "success",
          });
          dispatch(getListaPlanes());
        } else {
          enqueueSnackbar("Error al guardar el Plan", {
            variant: "error",
          });
        }
      });
    } else {
      enqueueSnackbar(
        "No puede dejar el nombre, ni la obra social, en blanco",
        {
          variant: "warning",
        }
      );
    }
  };

  useEffect(() => {
    if (costoServicio) {
      setCosto(costoServicio.costo);
      setCdPlan(costoServicio.plan.cd_plan);
      setCdTipoServicio(costoServicio.id.tipoServicio.cd_tipo_servicio);
      setServicio(costoServicio.servicio.cd_servicio);
    }
  }, [plan]);

  const nuevoPlan = () => {
    setCosto(0);
    setCdPlan(0);
    setCdTipoServicio(0);
    setServicio(0);

    dispatch(setCostoServicio({}));
  };

  return (
    <div>
      <Grid container>
        <Grid item lg={10} xs={8} md={8} sm={8} className={classes.headerForm}>
          Establecer Costo del Servicio
        </Grid>
        <Grid item lg={2} xs={4} md={4} sm={4} className={classes.buttonForm}>
          <Button variant="contained" color="default" onClick={nuevoPlan}>
            Nuevo
          </Button>
        </Grid>
      </Grid>
      <Grid container className={classes.gridForm}>
        <Grid item xs={12} md={12} sm={12} lg={12} style={{ marginTop: 15 }}>
          <FormControl
            variant="outlined"
            fullWidth
            className={classes.formControl}
          >
            <InputLabel id="demo-simple-select-outlined-label">Plan</InputLabel>
            <Select
              labelId="demo-simple-select-outlined-label"
              id="demo-simple-select-outlined"
              label="Instituci贸n"
              fullWidth
              value={cdPlan}
              onChange={(event) => setPlan(event.target.value)}
            >
              {listaPlanes &&
                listaPlanes.map((plan) => {
                  return (
                    <MenuItem value={plan.cd_plan}>
                      {plan.obraSocial.nombre} - {plan.nombre}
                    </MenuItem>
                  );
                })}
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs={12} md={12} sm={12} lg={12} style={{ marginTop: 15 }}>
          <FormControl
            variant="outlined"
            fullWidth
            className={classes.formControl}
          >
            <InputLabel id="demo-simple-select-outlined-label">
              Servicio
            </InputLabel>
            <Select
              labelId="demo-simple-select-outlined-label"
              id="demo-simple-select-outlined"
              label="Servicio"
              fullWidth
              value={cdServicio}
              onChange={(event) => setCdServicio(event.target.value)}
            >
              {listaServicios &&
                listaServicios.map((servicio) => {
                  return (
                    <MenuItem value={servicio.cd_servicio}>
                      {servicio.nombre}
                    </MenuItem>
                  );
                })}
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs={12} md={12} sm={12} lg={12} style={{ marginTop: 15 }}>
          <FormControl
            variant="outlined"
            fullWidth
            className={classes.formControl}
          >
            <InputLabel id="demo-simple-select-outlined-label">
              Tipo Servicio
            </InputLabel>
            <Select
              labelId="demo-simple-select-outlined-label"
              id="demo-simple-select-outlined"
              label="Instituci贸n"
              fullWidth
              value={cdTipoServicio}
              onChange={(event) => setCdTipoServicio(event.target.value)}
            >
              {listaTipoServicio &&
                listaTipoServicio.map((tipoServicio) => {
                  return (
                    <MenuItem value={tipoServicio.cd_tipo_servicio}>
                      {tipoServicio.nombre}
                    </MenuItem>
                  );
                })}
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs={12} md={12} sm={12} lg={12}>
          <TextField
            variant="outlined"
            type="number"
            margin="normal"
            label={"Costo $"}
            fullWidth
            value={costo}
            onChange={(event) => setCosto(event.target.value)}
          />
        </Grid>

        <Grid item xs={12} md={12} sm={12} lg={12}>
          <Button variant="contained" color="primary" onClick={guardarPlan}>
            Guardar
          </Button>
        </Grid>
      </Grid>
    </div>
  );
}
const useStyles = makeStyles((theme) => ({
  headerForm: {
    textAlign: "center",
    paddingTop: 20,
  },
  buttonForm: {
    paddingTop: 10,
  },
  gridForm: {
    padding: 10,
  },
}));
