import React, { useState, useEffect } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Grid from "@material-ui/core/Grid";
import TextField from "@material-ui/core/TextField";
import Button from "@material-ui/core/Button";
import { FormControl, InputLabel, Select, MenuItem } from "@material-ui/core";
import { useSelector, useDispatch } from "react-redux";
import { setPrestadora } from "actions/PrestadoraActions";
import { useSnackbar } from "notistack";
import { url_servidor } from "Utils/constants";
import { horarios } from "Utils/constants";
export default function PrestadoraForm() {
  const classes = useStyles();
  const dispatch = useDispatch();
  const { enqueueSnackbar } = useSnackbar();

  const [cdPrestadora, setCdPrestadora] = useState(1);
  const [nombre, setNombre] = useState("");
  const [direccion, setDireccion] = useState("");
  const [telefono, setTelefono] = useState("");
  const [logo, setLogo] = useState("");
  const [intervalo, setIntervalo] = useState("");
  const [horaDesde, setHoraDesde] = useState("");
  const [horaHasta, setHoraHasta] = useState("");

  const prestadora = useSelector((state) => state.prestadora.prestadora);

  const guardarHorario = () => {
    if (intervalo !== "") {
      fetch(url_servidor + "prestadora", {
        method: cdPrestadora !== 0 ? "PUT" : "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          cd_configuracion: cdConfiguracion,
          intervalo: intervalo,
          horaDesde: horaDesde,
          horaHasta: horaHasta,
        }),
      }).then(function (response) {
        if (response.status === 200) {
          enqueueSnackbar("Se guardó el Horario", {
            variant: "success",
          });
          dispatch(
            setHorario({
              cd_configuracion: cdConfiguracion,
              intervalo: intervalo,
              horaDesde: horaDesde,
              horaHasta: horaHasta,
            })
          );
        } else {
          enqueueSnackbar("Error al guardar el Horario", {
            variant: "error",
          });
        }
      });
    } else {
      enqueueSnackbar("No puede dejar el intervalo en blanco", {
        variant: "warning",
      });
    }
  };

  useEffect(() => {
    setCdPrestadora(prestadora.cd_prestadora);
    setNombre(prestadora.nombre);
    setDireccion(prestadora.direccion);
    setTelefono(prestadora.telefono);
    setLogo(prestadora.logo);
    setIntervalo(prestadora.intervalo);
    setHoraDesde(prestadora.horaDesde);
    setHoraHasta(prestadora.horaHasta);
  }, [prestadora]);

  return (
    <div>
      <Grid container>
        <Grid item lg={10} xs={8} md={8} sm={8} className={classes.headerForm}>
          Editar Horario
        </Grid>
        <Grid
          item
          lg={2}
          xs={4}
          md={4}
          sm={4}
          className={classes.buttonForm}
        ></Grid>
      </Grid>
      <Grid container className={classes.gridForm}>
        <Grid item xs={12} md={12} sm={12} lg={12}>
          <Grid container>
            <Grid item xs={12} sm={6} md={6} lg={6}>
              <FormControl
                variant="outlined"
                fullWidth
                className={classes.formControl}
              >
                <InputLabel id="demo-simple-select-outlined-label">
                  Hora Desde
                </InputLabel>
                <Select
                  labelId="demo-simple-select-outlined-label"
                  id="demo-simple-select-outlined"
                  label="Hora Desde"
                  fullWidth
                  value={horaDesde}
                  onChange={(event) => setHoraDesde(event.target.value)}
                >
                  {horarios &&
                    horarios.map((hora) => {
                      return <MenuItem value={hora}>{hora}</MenuItem>;
                    })}
                </Select>
              </FormControl>
            </Grid>
            <Grid item xs={12} sm={6} md={6} lg={6}>
              <FormControl
                variant="outlined"
                fullWidth
                className={classes.formControl}
              >
                <InputLabel id="demo-simple-select-outlined-label">
                  Hora Hasta
                </InputLabel>
                <Select
                  labelId="demo-simple-select-outlined-label"
                  id="demo-simple-select-outlined"
                  label="Hora Hasta"
                  fullWidth
                  value={horaHasta}
                  onChange={(event) => setHoraHasta(event.target.value)}
                >
                  {horarios &&
                    horarios.map((hora) => {
                      return <MenuItem value={hora}>{hora}</MenuItem>;
                    })}
                </Select>
              </FormControl>
            </Grid>
          </Grid>
        </Grid>
        <Grid item xs={12} md={12} sm={12} lg={12}>
          <TextField
            variant="outlined"
            margin="normal"
            label={"Intervalo (en minutos)"}
            fullWidth
            value={intervalo}
            onChange={(event) => setIntervalo(event.target.value)}
          />
        </Grid>
        <Grid item>
          <Button variant="contained" color="primary" onClick={guardarHorario}>
            Guardar
          </Button>
        </Grid>
      </Grid>
    </div>
  );
}
const useStyles = makeStyles((theme) => ({
  headerForm: {
    textAlign: "center",
    paddingTop: 20,
  },
  buttonForm: {
    paddingTop: 10,
  },
  gridForm: {
    padding: 10,
  },
}));
